mapper.delete:
DELETE FROM tpl_mifi_sim_t
WHERE iccid = #{iccid,jdbcType=VARCHAR}
and tenant_id = #{tenantId,jdbcType=VARCHAR}
and device_sn is null

sql.where:
tenant_id = #{param1.tenantId,jdbcType=VARCHAR}

  and iccid like CONCAT('%',#{param1.iccid,jdbcType=VARCHAR},'%')


  and merchant_code = #{param1.merchantCode,jdbcType=VARCHAR}


  and device_sn like CONCAT('%',#{param1.deviceSn,jdbcType=VARCHAR},'%')


  and carrier_code = #{param1.carrierCode,jdbcType=VARCHAR}


  and net_type = #{param1.netType,jdbcType=VARCHAR}


  and name_status = #{param1.nameStatus,jdbcType=VARCHAR}


  and online_status = #{param1.onlineStatus,jdbcType=VARCHAR}


  and flow_status = #{param1.flowStatus,jdbcType=VARCHAR}


  and sim_type = #{param1.simType,jdbcType=VARCHAR}


  and imei like CONCAT('%',#{param1.imei,jdbcType=VARCHAR},'%')
